document.addEventListener("DOMContentLoaded",()=>{new class{constructor(){this.header=document.querySelector(".header"),this.workSection=document.querySelector(".work"),this.steps=document.querySelectorAll(".step"),this.logo=document.querySelector(".logo-element"),this.initSwiper(),this.initObservers()}initSwiper(){new Swiper(".team__swiper.swiper",{slidesPerView:1,spaceBetween:20,navigation:{nextEl:".swiper-button-next--team",prevEl:".swiper-button-prev--team"},breakpoints:{991.98:{spaceBetween:20,slidesPerView:3}}}),new Swiper(".partners__swiper.swiper",{slidesPerView:1,spaceBetween:20,navigation:{nextEl:".swiper-button-next--partners",prevEl:".swiper-button-prev--partners"},breakpoints:{991.98:{spaceBetween:20,slidesPerView:3}}}),new Swiper(".portfolio__swiper.swiper",{slidesPerView:1,spaceBetween:20,navigation:{nextEl:".swiper-button-next--portfolio",prevEl:".swiper-button-prev--portfolio"},breakpoints:{991.98:{spaceBetween:20,slidesPerView:2}}})}createObserver(e,r,t=.5){e&&new IntersectionObserver((e,t)=>{e.forEach(e=>{e.isIntersecting&&(r(e.target),t.unobserve(e.target))})},{threshold:t}).observe(e)}initObservers(){this.createObserver(this.workSection,()=>this.activateSteps(),.65),this.createObserver(this.logo,e=>e.classList.add("in-view"),.5)}activateSteps(){try{this.steps.forEach(e=>{e.classList.add("in-view")})}catch(e){console.log(e)}}}});